mapper.select:
SELECT post_base.post_id AS postId, post_base.author_id AS authorId, post_base.title AS title, post_base.summary AS summary, post_base.cover_img AS coverImg, post_base.update_time AS updateTime, post_base.post_type AS postType, post_base.post_state AS postState, post_count.read_count AS readCount, post_count.praise_count AS praiseCount, post_count.favorite_count AS favoriteCount, post_count.comment_count AS commentCount FROM post_base INNER JOIN post_favorite ON post_base.post_id = post_favorite.post_id INNER JOIN post_count ON post_base.post_id = post_count.post_id WHERE post_favorite.favorite_state = 1 AND post_favorite.user_id = #{query.userId}
AND post_base.post_type=#{query.postType}
AND post_base.post_state=#{query.postState}
post_base.update_time
post_count.read_count
post_count.praise_count
post_count.favorite_count
post_count.comment_count
ASC
DESC
